$(document).ready(function () {
    $('#suppliers-list-datatable').DataTable().clear().destroy();
    var pageLang = $(this).data('language');
    var datatableLanguages = {
        'tr': '//cdn.datatables.net/plug-ins/1.11.3/i18n/tr.json',
        'en': '',
        'fr': '//cdn.datatables.net/plug-ins/1.11.3/i18n/fr_fr.json',
        'pt': '//cdn.datatables.net/plug-ins/1.11.3/i18n/pt_br.json',
        'de': '//cdn.datatables.net/plug-ins/1.11.3/i18n/de_de.json'
    };
    var s, e = [];
    0 < $("#suppliers-list-datatable").length &&
        (s = $("#suppliers-list-datatable").DataTable({
            serverSide: true,
            processing: true,
            filter: true,
            autoWidth: false,
            language: {
                url: datatableLanguages[pageLang]
            },
            responsive: !0,
            "ajax": {
                "url": "/api/supplier/GetSuppliersFiltered",
                "type": "POST",
                "datatype": "json"
            },
            columnDefs: [
                { orderable: !1, targets: [4] },
                { width: "1%", targets: 0 },
                { width: "120px", targets: 1 }
            ], 
            columns: [
                { "data": "supplier_ID", "name": "supplier_ID" },
                { "data": "supplier_Name", "name": "supplier_Name" },
                { "data": "createdDate", "name": "createdDate" },
                { "data": "lastModifiedDate", "name": "lastModifiedDate" },
                { "render": function (data, type, row) { return '<div class="center-align"><a href="page-users-edit' + row.supplier_ID + '"><i class="material-icons">edit</i></a><a href="page-users-view"><i class="material-icons">remove_red_eye</i></a><a href="javascript:void(0)" onclick="DeleteRow(' + row.supplier_ID + ');"><i class="material-icons">delete</i></a></div>' } }
            ]
        }));
    $(document).on("click", "#suppliers-list-datatable tr",
        function () {
            $(this).find("td").each(function () {
                e.push($(this).text().trim())
            }),
                localStorage.setItem("supplier_ID", e[0]),
                localStorage.setItem("supplier_Name", e[1]),
                localStorage.setItem("createdDate", e[2]),
                localStorage.setItem("lastModifiedDate", e[3])
        }),
        void 0 !== localStorage.supplier_ID &&
        (
            $(".users-view-id").html(localStorage.getItem("supplier_ID")),
            $(".users-view-username").html(localStorage.getItem("supplier_Name")),
            $(".users-view-created-date").html(localStorage.getItem("createdDate")),
            $(".users-view-verified").html(localStorage.getItem("lastModifiedDate"))
        )

});